import React, { Component } from 'react';
import BuscarLugar from '../componentes/BuscarLugar'; 

class Lugares extends Component {

  state = {
    termino : '',
    imagenes : []
  }

  consultarApi = () => {
    const termino = this.state.termino;
    const url = `https://pixabay.com/api/?key=24984773-abca2174cc871f7be4c6d6707&q=${termino}`;

    //console.log(url);
    fetch(url)
      .then(respuesta => respuesta.json)
      .then(resultado => this.setState({ imagenes : resultado.hits }) )
  }

  datosBusqueda = (termino) => {
    this.setState({
      termino
    }, () => {
      this.consultarApi();
    }) 
  }


  render(){
    return (
      <div classname="app container">
        <div classname="jumbotron">
          <p classname="lead text-center">Buscador de Lugares</p>
          <BuscarLugar
            datosBusqueda={this.datosBusqueda}
          />           
        </div>
        {this.state.termino}
      </div>
    );    
  }
}
/*
function Lugares() {
  return <h2>Lugares</h2>;
  }
*/
export default Lugares;
